        public void SolvePart1()
        {
            var (left, right) = ParseInputString(InputListstr);

            // sort 
            left.Sort();
            right.Sort();

            // get total distance
            var totalDistance = 0;

            for (var i = 0; i < left.Count; i++)
            {
                totalDistance += Math.Abs(left[i] - right[i]);
            }

            Console.WriteLine($"[Day 1 - Part 1]\ntotal distance = {totalDistance}");
        }

        public void SolvePart2()
        {
            var (left, right) = ParseInputString(InputListstr);
            var similarityScore = 0;

            foreach (var leftEntry in left)
            {
                var test = right.FindAll(entry => entry == leftEntry);
                similarityScore += leftEntry * right.FindAll(entry => entry == leftEntry).Count;
            }

            Console.WriteLine($"[Day 1 - Part 2]\ntotal similarity score = {similarityScore}");
        }

        public Tuple<List<int>, List<int>> ParseInputString(string input)
        {
            var left = new List<int>();
            var right = new List<int>();

            var lines = input.Replace("\r", "").Split('\n');

            foreach (var line in lines)
            {
                var content = line.Split(' ');
                if (content.Length == 0 || line.Length == 0) continue;

                left.Add(int.Parse(content[0]));
                right.Add(int.Parse(content[content.Length - 1]));
            }

            return Tuple.Create(left, right);
        }
    }
