        public void Run()
        {
            //SolvePart1();
            SolvePart2();
        }

        public void SolvePart1()
        {
            var reports = ParseInputString(InputStr);
            var validReports = reports.Count(report => IsReportValid(report));

            Console.WriteLine($"[Day 2 - Part 1]\nvalid reports = {validReports}");
        }
        public void SolvePart2()
        {
            var reports = ParseInputString(InputStr);
            var validReports = 0;

            foreach (var report in reports)
            {
                // get changes between levels
                var levelChanges = new List<int>();
                for (int i = 0; i < report.Count - 1; i++)
                {
                    levelChanges.Add(report[i] - report[i + 1]);
                }

                // find the level changes that cause errors
                var markedLCs = new List<int>();
                for (var lci = 0; lci < levelChanges.Count - 1; lci++)
                {
                    if (Math.Abs(levelChanges[lci]) >= 1 &&
                        Math.Abs(levelChanges[lci]) <= 3 &&
                        ((levelChanges[lci] < 0 && levelChanges[lci + 1] < 0) ||
                        (levelChanges[lci] > 0 && levelChanges[lci + 1] > 0))
                        )
                    {
                        continue;
                    }

                    // level has a problem
                    markedLCs.Add(lci);
                    markedLCs.Add(lci + 1);
                }

                markedLCs = markedLCs.Distinct().ToList();

                // exit early, if the report is ok
                if (markedLCs.Count == 0)
                {
                    validReports++;
                    continue;
                }

                // check if the report is valid without the erroneus levels
                var updatedReports = markedLCs.Select(lci => IsReportValid(RemoveAtInline(report, lci)) || IsReportValid(RemoveAtInline(report, lci + 1))).ToList();
                if (updatedReports.Any(reportValidity => reportValidity))
                {
                    validReports++;
                }
            }

            // ERROR answer is too high
            Console.WriteLine($"[Day 2 - Part 2]\nvalid reports = {validReports}");
        }

        private bool IsReportValid(List<int> report)
        {
            // get changes between levels
            var levelChanges = new List<int>();
            for (int i = 0; i < report.Count - 1; i++)
            {
                levelChanges.Add(report[i] - report[i + 1]);
            }

            // get changes not in range [1, 3]
            if (levelChanges.Select(x => Math.Abs(x)).Any(x => x < 1 || x > 3)) return false;

            // get differing directions
            if (!levelChanges.All(x => x < 0) && !levelChanges.All(x => x > 0)) return false;

            // report is save
            return true;
        }

        private List<T> RemoveAtInline<T>(List<T> list, int index)
        {
            if (index < 0 || index >= list.Count)
            {
                throw new ArgumentOutOfRangeException("Index is out of range.");
            }

            List<T> modifiedList = new List<T>(list);
            modifiedList.RemoveAt(index);

            // Return the modified list
            return modifiedList;
        }

        private List<List<int>> ParseInputString(string inputString)
        {
            var result = new List<List<int>>();
            var lines = inputString.Replace("\r", "").Split('\n');

            foreach (var line in lines)
            {
                var levels = line.Split(' ');
                if (levels.Length == 0 || line.Length == 0) continue;

                result.Add(levels.Select(level => int.Parse(level)).ToList());
            }
            return result;
        }
    }
